// Output comes as one item from getUpdates
const res = items[0]?.json;
if (!res || res.ok === false) return [];

// Use static data to remember last processed update_id
const data = $execution.getWorkflowStaticData('global');
let last = Number(data.last_update_id || 0);
let maxId = last;

const out = [];
for (const u of (res.result || [])) {
  if (typeof u.update_id !== 'number') continue;
  if (u.update_id <= last) continue; // skip old/duplicate
  if (u.update_id > maxId) maxId = u.update_id;

  const m = u.message || u.channel_post || u.edited_message || u.edited_channel_post;
  if (!m) continue;

  const text = (m.text || m.caption || '').trim();
  const photos = Array.isArray(m.photo) ? m.photo : [];
  const file_id = photos.length ? photos[photos.length - 1].file_id : null;

  out.push({
    json: {
      update_id: u.update_id,
      chat_id: m.chat.id,
      message_id: m.message_id,
      date: m.date,
      text,
      has_photo: !!file_id,
      file_id
    }
  });
}

// Save latest id so next poll does not reprocess
data.last_update_id = maxId;
return out;

